package com.conditional;

public class MaxOfTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number1 = 24;
		int number2 = 44;

		if (number1 > number2)
			System.out.println("Max is -> " + number1);
		else
			System.out.println("Max is -> " + number2);

	}

}
