package com.operators;

public class TernaryOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 100;
		int b = 120;
		int max = (a > b) ? a : b;
		System.out.println(max);

		int number = 25;
		boolean isEven = (number % 2 == 0) ? true : false;
		System.out.println(isEven);

	}

}
